define('YES', '<span style="color: rgb(4, 190, 2); font-weight : bold;">已开启</span>');
define('NO', '<span style="color: red; font-weight : bold;">未开启</span>');
if (@$_GET['phpinfo'] == '1') {
    if (function_exists('phpinfo')) phpinfo();
    else echo "PHPINFO函数已被禁用无法正常显示详细信息!";
    exit;
}

function memory_usage() { $memory  = ( ! function_exists('memory_get_usage')) ? '0' : round(memory_get_usage()/1024/1024, 2).'MB'; return $memory;}
$link = mysqli_connect("localhost", "root", "mysql");
if($link){
    $yes =  '<span style="color: rgb(4, 190, 2); font-weight : bold;">已连接</span>' ;
}else{
    $yes="密码错误";
}

$d = opendir(__DIR__);
$arr = array("System Volume Information",'$RECYCLE.BIN','.','..','.txt','phpMyAdmin','index.php','.htaccess','  ','.idea');
$row = array();
while($file = readdir($d)) {
    if (!in_array($file, $arr))
        $row[] = $file;
}
?>
<div class="container">
    <div class="row">
        <div class="col-sm-12">
            <table class="table">
                <thead>
                <tr><td colspan="4">
                             <?php
                             foreach($row as $v){
                                 $ss = ($v=='gitee' || $v=='github')?$v:"http://$v.api.shanliwawa.top";
                                 echo "<a href='$ss' class='btn btn-blue'>$v</a>" ;
                             }
                             ?>
                    </td>
                </tr>
                <tr>
                    <th><a href="javascript:;" id="s1">显示/隐藏</a></th>
