                    <td >openssl: <?php echo extension_loaded('openssl') ? YES : NO ?></td>
                    <td >mcrypt: <?php echo extension_loaded('mcrypt') ? YES : NO ?></td>
                </tr>
                <tr>
                    <td >上传限制:<?php  echo ini_get('upload_max_filesize')?></td>
                    <td >post限制:<?php echo ini_get('post_max_size')?></td>
                    <td >imap: <?php echo extension_loaded('imap') ? YES : NO ?></td>
                    <td >sendmail: <?php echo extension_loaded('standard') ? YES : NO ?></td>
                </tr>
                <tr>
                    <td >脚本执行时间:<?php  echo ini_get('max_execution_time').'s'?></td>
                    <td >socket时间:<?php echo  ini_get('default_socket_timeout').'s'?></td>
                    <td >xdebug: <?php echo extension_loaded('xdebug') ? YES : NO ?></td>
                    <td >memcache: <?php echo function_exists('memcache_connect') ? YES : NO ?></td>
                </tr>
                <tr>
                    <td >短标签:<?php  echo ini_get('short_open_tag')==1?YES:NO?></td>
                    <td >错误显示:<?php echo ini_get('display_errors')==1?YES:NO?></td>
                    <td >pdo: <?php echo extension_loaded('pdo') ? YES : NO ?></td>
                    <td >redis: <?php echo extension_loaded('redis') ? YES : NO ?></td>
                </tr>
                <tr>
                    <td >zend版本:<?php  echo zend_version();?></td>
                    <td >内存:<?php echo memory_usage();
                        echo "磁盘大小:".round((@disk_free_space('/')/(1024*1024*1024)),2)."G / ".round((@disk_total_space('/')/(1024*1024*1024)),2).'G';

                        ?></td>
                    <td >网站目录: <?php echo $_SERVER['DOCUMENT_ROOT']?></td>
                    <td >Mysql: <?php echo $yes ?></td>
                </tr>
                <tr><td colspan="4">
