    /**
     * 随机字符串
     * @param $rule 长度
     * @param $value 包含字符
     * @return bool|string
     */
    public static function string($rule, $value='')
    {

        $chars  = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ23456789';

        $value = $value ? $value : $chars;

        $string = '';

        if(false !== strpos($rule, '-')){

            list($min, $max) = explode('-', $rule);

            $max = $max > strlen($value) ? strlen($value)-1 : $max;

            return substr(str_shuffle($value),0, mt_rand($min, $max));

        }

        if(is_numeric($rule)){

            for ( $i = 0; $i < $rule; $i++ ){

                $string .= $value[mt_rand(0, strlen($value) - 1)];

            }

            return $string;

        }

    }
    /**
     * 随机数字
     * @param $rule
     * @return int|string
     */
    public static function number($rule,$value='')
    {

        $numbers  = '0123456789';

        $value = $value ? $value : $numbers;

        if(false !== strpos($rule, '-')){

            list($min, $max) = explode('-', $rule);

            return mt_rand($min, $max);

        }

        if(is_numeric($rule)){

            $number = '';

            for ( $i = 0; $i < $rule; $i++ ){
                $number .= $value[mt_rand(0, strlen($value) - 1)];

            }

            return $number;

        }

    }
