    /**
     * hex格式颜色模式转化为rgb格式
     * @param $hex
     * @return string
     */
    private static function hex2rgb($hex) {

        $color = str_replace('#','',$hex);

        $rgb = array(
            hexdec(substr($color,0,2)),
            hexdec(substr($color,2,2)),
            hexdec(substr($color,4,2))
        );

        return 'rgb('.implode(',', $rgb).')';

    }
    /**
     * 随机颜色
     * @param string $format 颜色格式
     * @return string
     */
    public static function color($format = 'hex')
    {

        $colors = array();

        for($i = 0;$i<6;$i++){
            $colors[] = dechex(rand(0,15));
        }

        $hex = '#'.implode('',$colors);

        switch ($format) {

            case 'hex':

                return $hex;

                break;

            case 'rgb':

                return self::hex2rgb($hex);

                break;

        }

    }
