    private static  function toarr($str){
        $leng = mb_strlen($str, 'utf-8');
        $titlearray = [];
        for ($i=0; $i<$leng; $i++)
        {
            $titlearray[] = mb_substr($str, $i, 1, 'utf-8');
        }
        return $titlearray;
    }
